func toggleReplyForm(commentID int) templ.ComponentScript {
	return templ.ComponentScript{
		Name: `__templ_toggleReplyForm_3632`,
		Function: `function __templ_toggleReplyForm_3632(commentID){document.querySelectorAll('.edit-form').forEach(f => f.classList.add('hidden'));
const forms = document.querySelectorAll('.reply-form');
const targetId = 'reply-form-' + commentID;
const target = document.getElementById(targetId);
if (!target) return;
const wasHidden = target.classList.contains('hidden');
forms.forEach(f => f.classList.add('hidden'));
if (wasHidden) {
target.classList.remove('hidden');
const ta = target.querySelector('textarea');
if (ta) ta.focus();
}
}`,
		Call:       templ.SafeScript(`__templ_toggleReplyForm_3632`, commentID),
		CallInline: templ.SafeScriptInline(`__templ_toggleReplyForm_3632`, commentID),
	}
}

